const productCard=document.querySelectorAll(".catalog-product-card"),productBuyBtns=document.querySelectorAll(".catalog-product-card__buy-btn"),modalWindow=document.querySelector(".modal-in-cart"),modalCloseBtns=modalWindow.querySelector(".modal-close-btn");[...productCard].forEach(e=>{e.addEventListener("mouseover",()=>{e.querySelector("img").style.display="none",e.querySelector(".catalog-product-card__popup").style.display="block"}),e.addEventListener("mouseout",()=>{e.querySelector("img").style.display="block",e.querySelector(".catalog-product-card__popup").style.display="none"})}),[...productBuyBtns].forEach(e=>{e.addEventListener("click",e=>{e.preventDefault(),modalWindow.classList.add("modal--active")})}),modalCloseBtns.addEventListener("click",()=>{modalWindow.classList.remove("modal--active")}),document.addEventListener("keydown",e=>{!modalWindow.classList.contains("modal--active")||"Esc"!==e.key&&"Escape"!==e.key||modalWindow.classList.remove("modal--active")});
